sub string_width_svg {

    my ($filename, $font_family_style, $strain_name_font_size) = @_;
    my $returnval = 0;
  
    foreach my $char (unpack("C*",$filename)) {
       if (defined $fonts{$font_family_style}) {$returnval+=$fonts{$font_family_style}->[$char-32];}
       else {$returnval+=$fonts{"Arial:Normal"}->[$char-32]; print "\nWarning: font:style is not defined in \%fonts in metrics module\n"; }
    }

    return ($returnval*$strain_name_font_size/1000);

}


sub string_height_svg {

    my ($font_family_style, $strain_name_font_size) = @_;
    my $individual_cha_height;

    $individual_cha_height = ($FontBBoxs{$font_family_style}->[3] - $FontBBoxs{$font_family_style}->[1])*$strain_name_font_size/1000 if defined $FontBBoxs{$font_family_style};  #llx lly urx ury;

    $individual_cha_height = ($FontBBoxs{"Arial:Normal"}->[3] - $FontBBoxs{"Arial:Normal"}->[1])*$strain_name_font_size/1000 if !defined $FontBBoxs{$font_family_style};  

    print "\nWarning: font:style is not defined in \%FontBBoxs in metrics module\n" if !defined $FontBBoxs{$font_family_style};  

    return $individual_cha_height;

}


